import React, { Component } from 'react';
import { MDBContainer, MDBBtn, MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter } from 'mdbreact';

class ModalPage extends Component {
state = {
  modal: false
}

toggle = () => {
  this.setState({
    modal: !this.state.modal
  });
}

render() {
  return (
    <MDBContainer>
      <MDBBtn onClick={this.toggle}>Modal</MDBBtn>
      <MDBModal id="modal_section" isOpen={this.state.modal} toggle={this.toggle} size="lg" centered>
        <MDBModalHeader toggle={this.toggle}>
          <div className="text-center modal_heading">
           <img src="images/checkerIcon.svg" alt="checker"/>
           <h1>Get Early Update</h1>
         </div>
          </MDBModalHeader>
        <MDBModalBody>
        <form id="modal_form" autocomplete="off">
            <div class="group">
              <input type="text" placeholder="Full Name" id="name"/>
              <label for="name">Full Name</label>
            </div>

            <div class="group">
              <input type="tel" placeholder="phone" id="Phone Number"/>
              <label for="phone">Phone Number</label>
            </div>
            <div class="group">

            </div>

        </form>
        </MDBModalBody>
        <MDBModalFooter>
          <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>
          <MDBBtn color="primary">Save changes</MDBBtn>
        </MDBModalFooter>
      </MDBModal>
    </MDBContainer>
    );
  }
}

export default ModalPage;
